                const response = JSON.parse(data);
                if (response.type === "CheckoutResponse") {
                    resolve(response);
                }
            });
        });
    },
    /**
     * Creates a new branch in the local repository at the given path.
     * @param {string} path - The file system path of the local Git repository.
